import { makeStyles } from '@mui/styles';
import React, { useContext, useEffect, useState } from 'react';
import { Cripto } from '../ContextCripto';
import { SingleCoin} from "../config/api";
import {  useParams } from 'react-router-dom';
import InfoCoin from '../components/InfoCoin';


const useStyles = makeStyles((theme) => ({
  container: {
    display: "flex",
    // [theme.breakpoints.down("md")]: {
    //   flexDirection: "column",
    //   alignItems:"center",
    // }
  },
  sidebar: {
    width: "30%",
    // [theme.breakpoints.down("md")]: {
    //   width:"100%",
    //},
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    marginTop: 25,
    borderRight:"2px solid grey"
  }
}))

const CoinPage = () => {
  const { id } = useParams();
  const [coin, setCoin] = useState([]);
  const { currency, symbol } = useContext(Cripto);
  const classes = useStyles();

  useEffect(() => {
    const fetchCoin = async () => {
      try {
        const response = await fetch(SingleCoin(currency));
        if (!response.ok) {
          throw new Error('Failed to fetch data');
        }
        const data = await response.json();
        setCoin(data.Data);
      } catch (error) {
        console.error("Error fetching coin data:", error);
      }
    };

    fetchCoin();
  }, [currency]);

  console.log(coin.length);

  // Verifica si `coin` es una matriz y tiene elementos antes de mapearlo
  const items = coin.map((coin) => {
    return (
      <div className={classes.container}>
        <div className={classes.sidebar}>
          <img
            key={coin?.CoinInfo?.Id}
            src={`https://www.cryptocompare.com${coin?.CoinInfo?.ImageUrl}`}
            alt={coin?.CoinInfo?.FullName}
            height="200"
            style={{ marginBottom: 20 }}
          />
        </div>
        {/*chart */}
        <InfoCoin coin={coin} />
      </div>
    );
  });

  return (
    <div>
      {coin}
    </div>
  );
}
export default CoinPage;
