import { makeStyles } from '@mui/styles';
import React, { useContext, useEffect, useState } from 'react';
import { Cripto } from '../../ContextCripto';
//import { TrendingCoins } from "../../config/api";


const useStyles = makeStyles((theme) => ({
  carrusel: {
        height: "50%",
        display: "flex",
        alignItems: "center",
        color: "black",
  },
}));


const Carrusel = () => {
  const [trending, setTrending] = useState([]);
  const classes = useStyles();
  const { currency } = useContext(Cripto);

  const fetchTrendingCoins = async () => {
    try {
      const urlProxy = 'https://cors-anywhere.herokuapp.com/';
      const urlObjetivo = 'https://pro-api.coinmarketcap.com/v1/cryptocurrency/listings/latest?convert=usd&limit=10&sort=volume_24h_desc&CMC_PRO_API_KEY=e6fee4f9-42ac-472d-8b7c-de89c507b922';;
      const urlCompleta = `${urlProxy}${urlObjetivo}`;
  
      const respuesta = await fetch(urlCompleta);
      if (!respuesta.ok) {
        throw new Error(`La solicitud a la API falló con el estado ${respuesta.status}`);
      }
      const datos = await respuesta.json();
      setTrending(datos);
    } catch (error) {
      console.err("Error obteniendo las criptomonedas en tendencia:", error);
      // Maneja el error apropiadamente
    }
  };
  
  console.log(trending);
  
  useEffect(() => {
    fetchTrendingCoins();
  }, [currency]);
    return (
        <div className={classes.carrusel}>Carrusel</div>
    )
}

export default Carrusel
