export const CoinList = (currency, page = 1, pageSize = 100) => {
  const baseUrl = 'https://api.coinmarketcap.com/v1/cryptocurrency/listings/latest';
  const params = {
    convert: currency,
    limit: pageSize,
    page: page,
  };

  return new URLSearchParams(params).toString()  // Construye la cadena de consulta
    .replace('?', `${baseUrl}?`); // Prefija la URL base
};

export const SingleCoin = (id) => {
  const baseUrl = 'https://api.coinmarketcap.com/v1/ticker/';
  return `${baseUrl}${id}`;
};

export const HistoricalChart = (id, days = 365, vs_currency) => {
  const baseUrl = 'https://api.coinmarketcap.com/v1/ohlcv/historical';
  const params = {
    id: id,
    vs_currency: vs_currency,
    to: Math.floor(Date.now() / 1000), // Marca de tiempo Unix para hoy
    days: days,
  };

  return new URLSearchParams(params).toString()
    .replace('?', `${baseUrl}?`); // Prefija la URL base
};

export const TrendingCoins = (currency) =>
  `https://api.coingecko.com/api/v3/trending/coins/markets?vs_currency=${currency}&order=gecko_desc&per_page=10&page=1&sparkline=false&price_change_percentage=24h`;




