import React, { useContext, useEffect, useState } from 'react'
import { CoinList } from '../config/api';
import { Cripto } from '../ContextCripto';
import { Container, TextField, ThemeProvider, Typography, createTheme } from '@mui/material';

const CoinsTable = () => {
    const [coins, setCoins] = useState([]);
    const [loading, setLoading] = useState(false);
    const { currency, symbol } = useContext(Cripto);

    const fetchCoins = async () => {
        setLoading(true);
        const response = await fetch(CoinList(currency));
        const  data  = await response.json();
        
        setCoins(data.Data);
        setLoading(false);


    };

    console.log(coins);

    useEffect(() => {
        fetchCoins();
        // eslint-disable-next-line
    }, [currency])
    
    const darkTheme = createTheme({
        palette: {
          mode: "dark",
        },
      });
    
    return (
        <ThemeProvider theme={darkTheme}>
            <Container style={{ textAlign: "center" }}>
                <Typography variant="h4" style={{
                    margin: 18,
                    fontFamily: "monospace",
                    color: "rgb(0, 150, 250)",
                    textShadow: "2px 1px 2px rgba(0, 0, 200, 1)"
                }}>
                    CryptoCurrency Prices by Market Cap
                </Typography>
                <TextField label="Searcho for a Crypto Currency..." variant="outlined" style={{marginBottom}} />
            </Container>
        </ThemeProvider>
    );
}

export default CoinsTable
