import React, { useContext, useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { Cripto } from '../ContextCripto';
import { SingleCoin } from '../config/api';
import { makeStyles } from '@mui/styles';
import InfoCoin from "../components/InfoCoin"

const useStyles = makeStyles((theme) => ({
  container: {
    display: "flex",
    // [theme.breakpoints.down("md")]: {
    //   flexDirection: "column",
    //   alignItems:"center",
    // }
  },
  sidebar: {
    width: "30%",
    // [theme.breakpoints.down("md")]: {
    //   width:"100%",
    //},
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    marginTop: 25,
    borderRight:"2px solid grey"
  }
}))

const CoinPage = () => {
  const { id } = useParams();
  const [coin, setCoin] = useState([]);
  const { currency, symbol } = useContext(Cripto);
  const classes = useStyles();

  const fetchCoin = async () => {
    const response = await fetch(SingleCoin(currency));
    const data = await response.json();
    setCoin(data.Data);
    console.log(coin);
  };

  useEffect(() => {
    fetchCoin();
    // eslint-disable-next-line
  }, [currency])
  // const items = coin.map((coin) => {
    return (
      <div className={classes.container}>
        <div className={classes.sidebar}>
          <img
            key={coin?.CoinInfo?.Id}
            src={`https://www.cryptocompare.com${coin.CoinInfo?.ImageUrl}`}
            alt={coin?.CoinInfo?.FullName}
            height="200"
            style={{ marginBottom: 20 }}
          />
        </div>
        {/*chart */}
        <InfoCoin coin={coin} />

      </div>
    )
//   })
}

export default CoinPage;
