import React, { useContext, useEffect, useState } from 'react'
import { Cripto } from '../ContextCripto';
import { HistoryChart } from '../config/api';
import { LinearProgress, ThemeProvider, createTheme, makeStyles } from '@mui/material';

const InfoCoin = ({coin}) => {
  const [histData, setHistData] = useState();
  const [days, setDays] = useState(1);
  const { currency, symbol } = useContext(Cripto);
  const [loading, setLoading] = useState(false);


  const fetchHistoryData = async () => {
    setLoading(true); // Marcar como cargando antes de hacer la solicitud
    try {
      const response = await fetch(HistoryChart(coin.SYMBOL, days, currency));
      const data = await response.json();
      setHistData(data);
    } catch (error) {
      console.error('Error fetching la data:', error);
      // Aquí puedes manejar el error de la manera que desees
    } finally {
      setLoading(false); // Marcar como no cargando después de la solicitud, independientemente del resultado
    }
  };

  useEffect(() => {
    fetchHistoryData();
    // eslint-disable-next-line
  }, [days, currency]);
  
  const darkTheme = createTheme({
    palette: {
      primary: {
        main: "#fff",
      },
      type: "dark",
    },
  });
  const useStyles = makeStyles(() => ({}));

  const classes = useStyles

  return (
    <ThemeProvider theme={darkTheme}>
      <div className={classes.container}>
        InfoCoin
        {loading && <LinearProgress style={{ backgroundColor: "rgb(0, 150, 250)" }} />} {/* Mostrar barra de progreso si está cargando */}
      </div>
    </ThemeProvider>
  )
}

export default InfoCoin;
