import { makeStyles } from '@mui/styles';
import React, { useContext, useEffect, useState } from 'react';
import { Cripto } from '../../ContextCripto';
import { TrendingCoins } from "../../config/api";
import AliceCarousel from 'react-alice-carousel';
import { Link } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
  container: {
    display: "flex",
  },
  sidebar: {
    width: "30%",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    marginTop: 25,
    borderRight: "2px solid grey",
  },
  heading: {
    fontWeight: "bold",
    marginBottom: 20,
    fontFamily: "Montserrat",
  },
  description: {
    width: "100%",
    fontFamily: "Montserrat",
    padding: 25,
    paddingBottom: 15,
    paddingTop: 0,
    textAlign: "justify",
  },
  marketData: {
    alignSelf: "start",
    padding: 25,
    paddingTop: 10,
    width: "100%",
    display: "flex",
    justifyContent: "space-around",
    [theme.breakpoints.down("md")]: {
      justifyContent: "flex-start",
      flexDirection: "column",
      alignItems: "center",
    },
  },
}));

const CoinPage = () => {
  const { id } = useParams();
  const [coin, setCoin] = useState({});
  const { currency, symbol } = useContext(Cripto);
  const classes = useStyles();

  const fetchCoin = async () => {
    try {
      const response = await fetch(SingleCoin(id, currency));
      if (!response.ok) {
        throw new Error('Failed to fetch data');
      }
      const data = await response.json();
      setCoin(data);
    } catch (error) {
      console.error("Error fetching coin data:", error);
    }
  };

  useEffect(() => {
    fetchCoin();
    // eslint-disable-next-line
  }, [currency, id]);

  if (!coin || !coin.CoinInfo) return <LinearProgress style={{ backgroundColor: "gold" }} />;

  const profit = coin?.DISPLAY?.[currency]?.CHANGEPCTDAY || 0;

  return (
    <div className={classes.container}>
      <div className={classes.sidebar}>
        <img
          src={`https://www.cryptocompare.com${coin.CoinInfo.ImageUrl}`}
          alt={coin.CoinInfo.FullName}
          height="200"
          style={{ marginBottom: 20 }}
        />
        <Typography variant="h3" className={classes.heading}>
          {coin.CoinInfo.FullName}
        </Typography>
        <Typography variant="subtitle1" className={classes.description}>
          {ReactHtmlParser(coin.CoinInfo.Description?.split(". ")[0])}.
        </Typography>
        <div className={classes.marketData}>
          <span style={{ display: "flex" }}>
            <Typography variant="h5" className={classes.heading}>
              Rank:
            </Typography>
            <Typography variant="h5" style={{ fontFamily: "Montserrat" }}>
              {coin.CoinInfo.SortOrder}
            </Typography>
          </span>
          <span style={{ display: "flex" }}>
            <Typography variant="h5" className={classes.heading}>
              Current Price:
            </Typography>
            <Typography variant="h5" style={{ fontFamily: "Montserrat" }}>
              {symbol}{coin.DISPLAY[currency].PRICE}
            </Typography>
          </span>
          <span style={{ display: "flex" }}>
            <Typography variant="h5" className={classes.heading}>
              Market Cap:
            </Typography>
            <Typography variant="h5" style={{ fontFamily: "Montserrat" }}>
              {symbol}{coin.DISPLAY[currency].MKTCAP}
            </Typography>
          </span>
        </div>
      </div>
      <InfoCoin coin={coin} />
    </div>
  );
};

export default CoinPage;


