import { makeStyles } from '@mui/styles';
import React, { useContext, useEffect, useState } from 'react';
import { Cripto } from '../ContextCripto';
import { SingleCoin} from "../config/api";
import {  useParams } from 'react-router-dom';
import InfoCoin from '../components/InfoCoin';
import { Typography } from '@mui/material';



const useStyles = makeStyles((theme) => ({
  container: {
    display: "flex",
    // [theme.breakpoints.down("md")]: {
    //   flexDirection: "column",
    //   alignItems:"center",
    // }
  },
  sidebar: {
    width: "30%",
    // [theme.breakpoints.down("md")]: {
    //   width:"100%",
    //},
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    marginTop: 25,
    borderRight:"2px solid grey"
  }
}))

const CoinPage = () => {
  const { id } = useParams();
  const [coinL, setCoin] = useState([]);
  const { currency, symbol } = useContext(Cripto);
  const classes = useStyles();

  useEffect(() => {
    const fetchSingleCoin = async () => {
      const response = await fetch(SingleCoin(id));
      const data = await response.json();
      console.log(data);
      setCoin(data.Data);
    };

    fetchSingleCoin();
    // eslint-disable-next-line 
  }, []);
  //console.log(coinL.[id]?.[currency]?.FROMSYMBOL);



    return (
      <div className={classes.container}>
        <div className={classes.sidebar}>
        <img
            key={coinL.ID_LEGACY}
            src={`${coinL.LOGO_URL}`}
            alt={coinL.NAME}
            height="200"
            style={{ marginBottom: 20 }}
          
        />
<Typography variant="subtitle1" className={classes.description}>
  {coinL?.ASSET_DESCRIPTION_SNIPPET && coinL.ASSET_DESCRIPTION_SNIPPET.split(". ")[0]}
</Typography>
        </div>
        {/*chart */}
        <InfoCoin coin={coinL}>

        </InfoCoin>
      </div>
    );

}
export default CoinPage;
