import React from 'react';
import { makeStyles } from '@mui/styles';
import { Container, Typography } from '@mui/material';

const useStyles = makeStyles(() => ({
  banner: {
    display: 'flex', // Convierte #banner en un contenedor flexible
    flexDirection: 'row', // Organiza los elementos verticalmente
    alignItems: 'center', // Centra los elementos horizontalmente
    justifyContent: 'center', // Centra los elementos verticalmente dentro del espacio disponible
  },
  tagline: {
    marginTop: '2rem', // Agrega margen superior para espaciar el título y el lema
  },
}));

const Banner = () => {
  const classes = useStyles();
  return (
    <div id="banner" className={classes.banner}>
      <Container id="bannerContent">
        <Typography variant="h2" style={{ fontWeight: 'bold', fontFamily: 'monospace' }}>
          CriptoToday
        </Typography>
        <Typography variant="subtitle2" className={classes.tagline} style={{ textTransform: 'capitalize', fontFamily: 'monospace', color: 'whitesmoke' }}>
          El futuro de las finanzas está en tus manos.
        </Typography>
      </Container>
    </div>
  );
};

export default Banner;
