import React, { useContext, useEffect, useState } from 'react'
import { CoinList } from '../config/api';
import { Cripto } from '../ContextCripto';
import { Container, LinearProgress, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, TextField, ThemeProvider, Typography, createTheme } from '@mui/material';

const CoinsTable = () => {
    const [coins, setCoins] = useState([]);
    const [loading, setLoading] = useState(false);
    const { currency, } = useContext(Cripto);
    const [search, setSearch] = useState("");

    const fetchCoins = async () => {
        setLoading(true);
        const response = await fetch(CoinList(currency));
        const  data  = await response.json();
        
        setCoins(data.Data);
        setLoading(false);


    };

    console.log(coins);

    useEffect(() => {
        fetchCoins();
        // eslint-disable-next-line
    }, [currency])
    
    const darkTheme = createTheme({
        palette: {
          mode: "dark",
        },
    });
    
    const handleSearch = () => {
        return coins.filter(
            (coin) =>
                (coin.CoinInfo?.FullName).toLowerCase().includes(search) || (coin.CoinInfo?.FullName).toLowerCase().includes(search)
        )
    }
    
    return (
        <ThemeProvider theme={darkTheme}>
            <Container style={{ textAlign: "center" }}>
                <Typography variant="h4" style={{
                    margin: 18,
                    fontFamily: "monospace",
                    color: "rgb(0, 150, 250)",
                    textShadow: "2px 1px 2px rgba(0, 0, 200, 1)"
                }}
                onChange={(e)=>setSearch(e.target.value)}>
                    CryptoCurrency Prices by Market Cap
                </Typography>
                <TextField label="Find you Crypto Currency..." variant="outlined" style={{ marginBottom: 20, width: "100%" }} />
                <TableContainer>
                    {
                        loading ? (
                            <LinearProgress style={{ background: "rgb(0, 150, 250)" }} />)
                            : (
                                <Table>
                                    <TableHead style={{ backgroundColor: "rgb(0, 150, 250)" }}>
                                        <TableRow>
                                            {["Coin", "Price", "24h Change", "Market Cap"].map((head) => (
                                                <TableCell
                                                    style={{
                                                        color: "black", fontWeight: "700", fontFamily: "monospace"
                                                    }}
                                                    key={head} aling={head === "Coin" ? "" : "right"}>
                                                    {head}

                                                </TableCell>
                                            ))}
                                        </TableRow>

                                    </TableHead>
                                    <TableBody>
                                        {handleSearch()}
                                    </TableBody>
                                </Table>
                            )
                    }
                </TableContainer>
            </Container>
        </ThemeProvider>
    );
}

export default CoinsTable
