import React, { useState, useEffect } from 'react';
import { Chart } from 'chart.js'; //Librería de gráficos

const InfoCoin = ({ coin, currency }) => {
  const [data, setData] = useState([]); //Almacena los datos de la API
  const [chart, setChart] = useState(null); //Almacena la referencia al gráfico
  
  
  const fetchData = async () => {
    const response = await fetch(HistoryChart(coin.SYMBOL, currency));
    const data = await response.json();
    setData(data.Data);
  };

  useEffect(() => {
    // Carga los datos al renderizar el componente
    HistoryChart();
    

    // Crea el gráfico al renderizar el componente
    const createChart = () => {
      const ctx = document.getElementById('chart').getContext('2d');
      const myChart = new Chart(ctx, {
        type: 'line',
        data: {
          labels: data.map(d => d.time),
          datasets: [{
            label: coin,
            data: data.map(d => d.close),
          }],
        },
      });
      setChart(myChart);
    };

    if (data.length > 0) {
      createChart();
    }

  }, [data, coin, currency, fetchData]); // Agregar fetchData a la lista de dependencias

  return (
    <div>
      <h2>{coin} ({currency})</h2>
      <canvas id="chart" width="400" height="200"></canvas>
      {chart && (
        <div>
          <h3>Order Book</h3>
          <p>Aquí se mostrará información del order book.</p>
        </div>
      )}
    </div>
  );

};

export default InfoCoin;

