const Cripto = createContext()


const ContextCripto = ({children}) => {
const [currency, setCurrency] = useState("USD")
const [symbol, setSymbol] = useState("$")
  
  return (
      <Cripto.Provider>
          {children}
    </Cripto.Provider>
  )
}

export default ContextCripto


export const CriptoState = () => {
  useContext(Cripto)
}
