  //       alt={coin.CoinInfo.FullName}
  //     />
  //   );
  // });

  // console.log(items);





  return (
    <div  className={classes.carrusel}>
      {<AliceCarousel
      mouseTracking
      infinite
      autoPlayInterval={1000}
      animationDuration={1500}
      disableDotsControls/>}
      
    </div>
  )
}

