//improvisando ver si se puede colocar un Order book
import React, { useState, useEffect, useContext } from 'react';
import { Chart } from 'chart.js/auto'; 
import { HistoricalChart } from '../config/api';
import { Line } from 'react-chartjs-2';
import { Cripto } from '../ContextCripto';

const InfoCoin = ({coin}) => {
  const [data, setData] = useState([]);
  const {currency, setCurrency} = useContext(Cripto);
  
  console.log(currency);
  
  const fetchData = async () => {
    const response = await fetch(HistoricalChart(coin,currency));
    const responseData = await response.json();
    setData(responseData.data);
  };


  useEffect(() => {
    fetchData();
    // eslint-disable-next-line
  }, [coin]); 
  
  console.log(coin);
  
  return (
    <div style={{ width:"50%" }}>
      <h2 style={{textAlign:"center"}}>{coin.SYMBOL}</h2>
      <div style={{ textAlign: "center" }}>
        <Line></Line>
          <h3>Order Book</h3>
          <p>Aquí se mostrará información del order book.</p>
        </div>
    </div>
  );
};

export default InfoCoin;
