import { makeStyles } from '@mui/styles';
import React, { useContext, useEffect, useState } from 'react';
import { Cripto } from '../../ContextCripto';

const useStyles = makeStyles((theme) => ({
  carrusel: {
    height: "50%",
    display: "flex",
    alignItems: "center",
    color: "black",
  },
  moneda: {
    marginRight: 10,
    padding: 10,
    border: "1px solid #ccc",
  },
}));

const Carrusel = () => {
  const [trending, setTrending] = useState([]);
  const classes = useStyles();
  const { currency } = useContext(Cripto);

  const fetchTrendingCoins = async () => {
    try {
      const response = await fetch(TrendingCoins(currency));
      const data = await response.json();
      setTrending(data);
    } catch (error) {
      console.error(error);
      // Mostrar mensaje de error al usuario
    }
  };

  useEffect(() => {
    fetchTrendingCoins();
  }, [currency]);

  return (
    <div className={classes.carrusel}>
      {trending.map((moneda) => (
        <div key={moneda.id} className={classes.moneda}>
          <h2>{moneda.name}</h2>
          <p>Precio: {moneda.price}</p>
          <p>Cambio 24h: {moneda.change24h}</p>
        </div>
      ))}
    </div>
  );
};

export default Carrusel;

