import { makeStyles } from '@mui/styles';
import React, { useContext, useEffect, useState } from 'react';
import { Cripto } from '../../ContextCripto';
//import { TrendingCoins } from "../../config/api";


const useStyles = makeStyles((theme) => ({
  carrusel: {
        height: "50%",
        display: "flex",
        alignItems: "center",
        color: "black",
  },
}));


const Carrusel = () => {
  const [trending, setTrending] = useState([]);
  const classes = useStyles();
  const { currency } = useContext(Cripto);

  const fetchTrendingCoins = async () => {
    try {
      const response = await fetch(`https://api.coinmarketcap.com/v1/cryptocurrency/listings/latest?convert=usd&limit=10&sort=volume_24h_desc`);
      if (!response.ok) {
        throw new Error(`API request failed with status ${response.status}`);
      }
      const data = await response.json();
      setTrending(data);
    } catch (error) {
      console.error("Error fetching trending coins:", error);
      // Maneja el error de forma adecuada, como mostrar un mensaje al usuario.
    }
  };
  
  console.log(trending);
  
  useEffect(() => {
    fetchTrendingCoins();
  }, [currency]);
    return (
        <div className={classes.carrusel}>Carrusel</div>
    )
}

export default Carrusel
