import React, { useState, useEffect } from 'react';
import { Chart } from 'chart.js/auto'; // Asegúrate de importar Chart.js correctamente
import { HistoryChart } from '../config/api';

const InfoCoin = ({ coin, currency }) => {
  const [data, setData] = useState([]); // Almacena los datos de la API
  const [chart, setChart] = useState(null); // Almacena la referencia al gráfico
  
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(HistoryChart(coin, currency));
        const responseData = await response.json();
        setData(responseData.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };
  
    fetchData();
  }, [coin, currency]); // Solo coin y currency como dependencias
   // coin, currency y chart deben mantenerse como dependencias
  
  console.log(coin);
  
  return (
    <div>
      <h2 style={{textAlign:"right",marginRight}}>{coin.SYMBOL}{currency}</h2>
        <div style={{textAlign:"center"}}>
          <h3>Order Book</h3>
          <p>Aquí se mostrará información del order book.</p>
        </div>
    </div>
  );
};

export default InfoCoin;

