import { makeStyles } from '@mui/styles';
import React, { useContext, useEffect, useState } from 'react';
import { Cripto } from '../../ContextCripto';
import { TrendingCoins } from "../../config/api";
import AliceCarousel from 'react-alice-carousel';


const useStyles = makeStyles((theme) => ({
  carrusel: {
        height: "50%",
        display: "flex",
        alignItems: "center",
        color: "black",
  },
}));


const Carrusel = () => {
  const [trending, setTrending] = useState([])
  const classes = useStyles();
  const {currency} = useContext(Cripto);
  
  useEffect(() => {
    const fetchTrendingCoins = async () => {
      const { data } = await fetch(TrendingCoins(currency));
      //const { data } = await (await fetch(TrendingCoins(currency))).json();
      setTrending(data);
      console.log(setTrending(data));
    };


    
  
    fetchTrendingCoins();
  }, [currency]);

  const responsive = {
    0: {
      items: 2,
    },
    512: {
      items: 4,
    },
  };
  
  console.log(trending);

    return (
      <div className={classes.carrusel}>
        <AliceCarousel
          mouseTracking
          infinite
          autoPlayInterval={1000}
          animationDuration={1500}
          disableDotsControls
              responsive={responsive}
        >

        </AliceCarousel>
        </div>
    )
}

export default Carrusel
