import Chart from 'chart.js/auto';
import React, { useState, useEffect, useContext } from 'react';
import { Line } from 'react-chartjs-2';
import { HistoricalChart } from '../config/api';
import { Cripto } from '../ContextCripto';
import { Button, CircularProgress } from '@mui/material';

const InfoCoin = ({ coin }) => {
  const [historicalData, setHistoricalData] = useState(null);
  const [days, setDays] = useState(30); // Estado para almacenar el número de días seleccionados
  const { currency } = useContext(Cripto);

  const fetchHistoricalData = async () => {
    try {
      const response = await fetch(HistoricalChart(coin.SYMBOL, currency, days));
      const responseData = await response.json();
      setHistoricalData(responseData.Data.Data);
    } catch (error) {
      console.error('Error fetching historical data:', error);
    }
  };

  useEffect(() => {
    fetchHistoricalData();
    // eslint-disable-next-line
  }, [coin.SYMBOL, currency, days]); // Se ejecuta nuevamente cuando se cambia el número de días

  const generateChartData = () => {
    if (!historicalData) return {};

    const labels = historicalData.map(item => {
      const date = new Date(item.time * 1000); // Convertir el tiempo Unix a milisegundos
      return date.toLocaleString('en-EN', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' });
  });
    const data = historicalData.map(item => item.close);

    return {
      labels: labels,
      datasets: [
        {
          label: 'Crypto Price',
          data: data,
          fill: false,
          borderColor: 'rgb(0, 150, 250)',
          tension: 1 
        }
      ]
    };
  };
  const options = {
    scales: {
      x: {
        ticks: {
          fontColor: 'blue', // Color de la fuente
          padding: 10 // Espacio entre los ticks y el borde del gráfico
        },
        min: 0, // Valor mínimo en el eje x
        max: 100, // Valor máximo en el eje x
        reverse: false, // Orden de los ticks en el eje x
        display: true // Mostrar los ticks del eje x
      }
    }
  };;

  const handleDaysChange = (newDays) => {
    setDays(newDays);
  };

  return (
    <div>
      <h2 style={{ fontWeight: "bold", fontFamily: "monospace",textAlign:"center",fontSize:"50px" }}>Chart of {coin.SYMBOL}</h2>
      <div>
        {historicalData ? (
          <Line data={generateChartData()} height={600} width={1000} options={options} />
        ) : (
          <CircularProgress style={{ color: "rgb(0, 150, 250)" }} size={250} thickness={1} />
        )}
      </div>
        <div>
            volume of {coin.SYMBOL}
        </div>
      <div style={{textAlign:"center"}}>
        <Button
          style={{marginRight:"3vh"}}
          size='small'
          variant="contained"
          onClick={() => handleDaysChange(1)}
          disabled={days === 1} // Botón "Today"
        >
          Today
        </Button>
        <Button
          style={{marginRight:"3vh"}}
          size='small'
          variant="contained"
          onClick={() => handleDaysChange(7)}
          disabled={days === 7} // Botón "Last 7 days"
        >
          Last 7 days
        </Button>
        <Button
          style={{marginRight:"3vh"}}
          size='small'
          variant="contained"
          onClick={() => handleDaysChange(30)}
          disabled={days === 30} // Botón "Last 30 days"
          >
          Last 30 days
        </Button>
        </div>
    </div>
  );
};

export default InfoCoin;

