import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import { HistoricalChart, SingleCoin } from '../config/api';

const InfoCoin = () => {
  // Estado para almacenar los datos del gráfico
  const [chartData, setChartData] = useState({});
  const [coin, setCoin] = useState({});

  // Función para obtener datos del gráfico
  const fetchChartData = async () => {
    try {
      // Aquí deberías realizar una solicitud a una API que proporciona datos de criptomonedas
      // Por ejemplo, CryptoCompare u otra API de tu elección
      // Supongamos que los datos están en el formato adecuado para Chart.js
      const response = await fetch((coin));
      const {data} = await response.json();
      setCoin(data)
      console.log(chartData)

      // Actualizar el estado con los datos del gráfico
      setChartData({
        labels: data.labels,
        datasets: [
          {
            label: 'Precio de la criptomoneda',
            data: data.prices,
            fill: false,
            borderColor: 'rgb(75, 192, 192)',
            tension: 0.1,
          },
        ],
      });
    } catch (error) {
      console.error('Error fetching chart data:', error);
    }
  };

  useEffect(() => {
    // Llamar a la función para obtener datos del gráfico cuando el componente se monta
    fetchChartData();
  }, [coin]);

  return (
    <div>
      <h2>Gráfico de precio de criptomoneda</h2>
      <div>
        {/* Renderizar el gráfico de línea */}
        <Line data={chartData} />
      </div>
    </div>
  );
};

export default InfoCoin;

