import { makeStyles } from '@mui/styles';
import React, { useContext, useEffect, useState } from 'react';
import { Cripto } from '../ContextCripto';
import { SingleCoin} from "../config/api";
import {  useParams } from 'react-router-dom';
import InfoCoin from '../components/InfoCoin';
import { Typography } from '@mui/material';



const useStyles = makeStyles((theme) => ({
  container: {
    display: "flex",
    // [theme.breakpoints.down("md")]: {
    //   flexDirection: "column",
    //   alignItems:"center",
    // }
  },
  sidebar: {
    width: "30%",
    // [theme.breakpoints.down("md")]: {
    //   width:"100%",
    //},
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    marginTop: 25,
    borderRight:"2px solid grey"
  },
  
}))

const CoinPage = () => {
  const { id } = useParams();
  const [coinL, setCoin] = useState([]);
  const { currency, symbol } = useContext(Cripto);
  const classes = useStyles();

  useEffect(() => {
    const fetchSingleCoin = async () => {
      const response = await fetch(SingleCoin(id));
      const data = await response.json();
      console.log(data);
      setCoin(data.Data);
    };

    fetchSingleCoin();
    // eslint-disable-next-line 
  }, []);
  //console.log(coinL.[id]?.[currency]?.FROMSYMBOL);



    return (
      <div className={classes.container}>
        <div className={classes.sidebar}>
        <img
            key={coinL.ID_LEGACY}
            src={`${coinL.LOGO_URL}`}
            alt={coinL.NAME}
            height="200"
            style={{ marginBottom: 20 }}
          
          />
          <Typography variant="h3" className={classes.heading}>
            {coinL?.NAME}
          </Typography>
          <Typography variant="subtitle1" style={{ paddingLeft: '20px' }} className={classes.description}>
            {`${(coinL?.ASSET_DESCRIPTION_SUMMARY)}`} 
          </Typography>
          <div className={classes.marketData}>
            <span style={{ display: "flex" }}>
              <Typography variant="h5" className={classes.heading}>
                Rank:
              </Typography>
              &nbsp; &nbsp;
              <Typography variant="h5" style={{ fontFamily: "monospace" }}>
                {coinL.ID}
              </Typography>
            </span>
            <span style={{ display: "flex" }}>
              <Typography variant="h5" className={classes.heading}>
                Current Price:
              </Typography>
              <Typography variant='h5' style={{ fontFamily: "monospace" }}>
                 {symbol}{((coinL.PRICE_USD).toFixed(2))}
              </Typography>
              &nbsp; &nbsp;
            </span>
            <span style={{display:"flex"}}>
              <Typography variant="h5" className={classes.heading}>
                Market Cap:
              </Typography>
              &nbsp; &nbsp;
              <Typography variant="h5" style={{ fontFamily:"monospace" }}>
                {symbol}{(coinL.CIRCULATING_MKT_CAP_USD).toFixed(2)}
              </Typography>
            </span>
          </div>
        </div>
        <InfoCoin coin={coinL}>
        </InfoCoin>
      </div>
    );

}
export default CoinPage;
