import { makeStyles } from '@mui/styles';
import React, { useContext, useEffect, useState } from 'react';
import { Cripto } from '../../ContextCripto';
//import { TrendingCoins } from "../../config/api";


const useStyles = makeStyles((theme) => ({
  carrusel: {
        height: "50%",
        display: "flex",
        alignItems: "center",
        color: "black",
  },
}));


const Carrusel = () => {
  const [trending, setTrending] = useState([]);
  const classes = useStyles();
  const { currency } = useContext(Cripto);

  const fetchTrendingCoins = async () => {
    const response = await fetch('https://api.coingecko.com/api/v3/trending/coins/markets?vs_currency=${currency}&order=gecko_desc&per_page=10&page=1&sparkline=false&price_change_percentage=24h');
    const data = await response.json();
    setTrending(data);
  };
  
  console.log(trending);
  
  useEffect(() => {
    fetchTrendingCoins();
  }, [currency]);
    return (
        <div className={classes.carrusel}>Carrusel</div>
    )
}

export default Carrusel
