import React, { useContext, useEffect, useState } from 'react'
import { Cripto } from '../ContextCripto';
import { HistoryChart } from '../config/api';
import { LinearProgress } from '@mui/material';

const InfoCoin = ({coin}) => {
  const [histData, setHistData] = useState();
  const [days, setDays] = useState(1);
  const { currency, symbol } = useContext(Cripto);

  const fetchHistoryData = async () => {
    const response = await fetch(HistoryChart((coin.SYMBOL), days, currency))
    const data = await response.json();
    console.log(data);
    setHistData(data);
  }

  useEffect(() => {
    fetchHistoryData();
    // eslint-disable-next-line
  }, [days]);
  return (
    <div>
      {loading && <LinearProgress />} {/* Mostrar barra de progreso si est√° cargando */}
      InfoCoin
    </div>
  )
}

export default InfoCoin;
